      const activatorLeft = (this.attach !== false ? a.offsetLeft : a.left) || 0
      const minWidth = Math.max(a.width, c.width)
      let left = 0
      left += activatorLeft
      if (this.left || (this.$vuetify.rtl && !this.right)) left -= (minWidth - a.width)
